import React, { FC, useState } from "react";
import { Container, Row, Button } from "react-bootstrap";
import PaginationButtons from "../components/ui/PaginationButtons";
import MySelect from "../components/ui/select/MySelect";
import { IOption, IPost } from "../models/types";
import PostItem from "../components/items/PostItem";
import { postAPI } from "../services/PostService";

const PostApiPage = () => {
  // PAGINATION
  //================================================================================

  const { data } = postAPI.useFetchAllPostQuery();
  let totalCount = 0;

  if (data) {
    totalCount = data.length;
  }

  const [limit, setLimit] = useState<number>(5);
  const [page, setPage] = useState<number>(1);
  let countPage = Math.ceil(totalCount / limit);

  const pages: number[] = [];
  for (let i = 0; i < countPage; i++) {
    pages.push(i + 1);
  }

  const { data: posts, error, isLoading } = postAPI.useGetPostByPageQuery(page);
  // PAGINATION
  //================================================================================

  const [createPost, { error: createError }] = postAPI.useCreatePostMutation();
  const [updatePost, { error: updateError }] = postAPI.useUpdatePostMutation();
  const [deletePost, { error: deleteError }] = postAPI.useDeletePostsMutation();

  const handleUpdate = (post: IPost) => {};
  const handleRemove = (post: IPost) => {};

  return (
    <Container className="card mt-6">
      <Row>
        {posts &&
          posts.map((post) => <PostItem key={post.id} post={post} remove={handleRemove} update={handleUpdate} />)}
      </Row>
    </Container>
  );
};

export default PostApiPage;
