import { FC, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { useAppDispanch, useAppSelector } from "../hooks/redux";
import { fetchAsyncThunkUsers } from "../store/reducers/ActionCreater";

const UserThunkPage: FC = () => {
  const dispatch = useAppDispanch();

  const { users, isLoading, error } = useAppSelector((state) => state.userAsyncThunkReducer);

  // Когда мы в ActionCreater.ts используем createAsyncThunk,
  // то пишем fetchAsyncThunkUsers() со скобками
  useEffect(() => {
    dispatch(fetchAsyncThunkUsers());
  }, [dispatch]);

  return (
    <Container className="mt-6 mb-4">
      <h3 className="textCenter mb-5">
        Используем redux toolkit, создаём userAsyncThunkReducer с помощью функции createSlice()
      </h3>

      <Row className="mt-4">
        <Col md={12}>
          <h3 className="textCenter mb-5"> Юзеры2 </h3>
          {isLoading && <h3 className="textCenter mb-5 colorRed">Идёт загрузка</h3>}
          {error && <h3 className="textCenter mb-5 colorRed"> {error} </h3>}
          {JSON.stringify(users, null, 2)}
        </Col>
      </Row>
    </Container>
  );
};

export default UserThunkPage;
