import React, { useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import "./App.css";
import { useAppDispanch, useAppSelector } from "./hooks/redux";
import { fetchAsyncThunkUsers, fetchUsers } from "./store/reducers/ActionCreater";

function App() {
  const dispatch = useAppDispanch();
  const { users, isLoading, error } = useAppSelector((state) => state.userReducer);
  const {
    users: users2,
    isLoading: isLoading2,
    error: error2,
  } = useAppSelector((state) => state.userAsyncThunkReducer);

  useEffect(() => {
    fetchUsers(dispatch);
    dispatch(fetchAsyncThunkUsers());
  }, [dispatch]);

  return (
    <Container className="mt-6 mb-4">
      <h3 className="textCenter mb-5">Значение </h3>

      <Row className="mt-4">
        <Col md={3}>
          <h3 className="textCenter mb-5"> Юзеры </h3>
          {isLoading && <h3 className="textCenter mb-5 colorRed">Идёт загрузка</h3>}
          {error && <h3 className="textCenter mb-5 colorRed"> {error} </h3>}
          {JSON.stringify(users, null, 2)}
        </Col>

        <Col md={3}>
          <h3 className="textCenter mb-5"> Юзеры2 </h3>
          {isLoading2 && <h3 className="textCenter mb-5 colorRed">Идёт загрузка</h3>}
          {error2 && <h3 className="textCenter mb-5 colorRed"> {error2} </h3>}
          {JSON.stringify(users2, null, 2)}
        </Col>

        <Col md={3}>
          <h3 className="textCenter mb-5"> Юзеры </h3>
          {/* {isLoading && <h3 className="textCenter mb-5 colorRed">Идёт загрузка</h3>}
          {error && <h3 className="textCenter mb-5 colorRed"> {error} </h3>}
          {JSON.stringify(users, null, 2)} */}
        </Col>

        <Col md={3}>
          <h3 className="textCenter mb-5"> Юзеры </h3>
          {/* {isLoading && <h3 className="textCenter mb-5 colorRed">Идёт загрузка</h3>}
          {error && <h3 className="textCenter mb-5 colorRed"> {error} </h3>}
          {JSON.stringify(users, null, 2)} */}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
